

// text show function 
function textShow(input, element, className) {
    if(input.type == 'password'){
        input.type = 'text';
        toggleNav(element, className);
    }
    else {
        input.type = 'password';
        toggleNav(element, className);
    }
}


// class toggle function

function toggleNav(element, toggleName) {
    element.classList.toggle(toggleName);
}

// error functions

function popAlerts(message) {
    let alerts = document.createElement('div');
    alerts.classList.add('alerts');
    alerts.innerHTML = message;
    $('.alertsMessages').append(alerts);
    timer = setTimeout(function () {
        alerts.remove();
    }, 3000);
    removeAlerts();
}

function removeAlerts() {
    $('.alertsMessages>.alerts>.con>.canAlert').click(function() {
        clearTimeout(timer);
        $(this)[0].parentElement.parentElement.remove();
    });
}


// file upload 

function uploadSuccess(element, classAddElement, textAddElement, className, upload){
    name = element.val().split('\\').pop().split('.')[0];
	exe = element.val().split('.').pop();
    fileName = name.length < 15 ? name+' ('+exe+')' : name.slice(0, 15)+'...('+exe+')';
    textAddElement.html(fileName);
    if(upload == true) {
        classAddElement.addClass(className);
        textAddElement.html(fileName);
    }
    else {
        classAddElement.removeClass(className);
        textAddElement.html('upload Image');
    }
    
}